func getCellFromHex(lendingCode string) *cell.Cell {
	codeCell, err := GetCellFromHex(lendingCode)
	if err != nil {
		panic(err)
	}
	return codeCell
}

func GetCellFromHex(data string) (*cell.Cell, error) {
	boc, err := hex.DecodeString(data)
	if err != nil {
		return nil, err
	}
	code, err := cell.FromBOC(boc)
	if err != nil {
		return nil, err
	}
	return code, nil
}

