func (a Asset) Sha256Hash() *big.Int {
	h := sha256.New()
	_, err := h.Write([]byte(a))
	if err != nil {
		panic(err)
	}
	return big.NewInt(0).SetBytes(h.Sum(nil))
}

func (a Asset) ID() string {
	return a.Sha256Hash().String()
}

